import { createStore } from 'vuex'
import axios from "axios"

export default createStore({
  state: {
    notes: [],
  },
  getters: {
    allNotes: (state) => state.notes
  },
  actions: {
    async getNotes({ commit }) {
      const res = await axios.get("https://post-it-7m1i.onrender.com/notes");
      commit('setNotes', res.data.notes)
    },
    async submitNotes({ commit }, title, description) {
      const res = await axios.post("https://post-it-7m1i.onrender.com/notes", { title, completed: false }, { description, completed: false }).then(() => {
        this.getNotes();
      });
      commit('newPost', res.data);
    }
  },
  mutations: {
    setNotes: (state, notes) => state.notes = notes,
    newPost: (state, note) => state.notes.unshift(note)
  },
  modules: {
  }
})
